                }
            ]
        });
        $('#example2 tfoot th').each(function () {
            var title = $(this).text();
            $(this).html('<input type="text" placeholder="Search" class="form-control bottom-search"/>');
        });

        // DataTable
        var table = $('#example2').DataTable();

        // Apply the search
        table.columns().every(function () {
            var that = this;

            $('input', this.footer()).on('keyup change', function () {
                if (that.search() !== this.value) {
                    that
                        .search(this.value)
                        .draw();
                }
            });
        });
        /* $('#example2 tfoot tr').insertAfter($('#example2 thead tr'));*/

        $("#class_id").change(function () {
            var class_id = $(this).val();
            //$("#data_table_body").empty();
            var dataString = 'class_id=' + class_id;
            $.ajax
            ({
                type: "POST",
                dataType: 'json',
                url: "/Teachers/getAssignedAllSubjects",
                data: dataString,
                cache: false,
                success: function (objects) {
                    if ($.fn.DataTable.isDataTable('#example2')) {
                        $('#example2').DataTable().destroy();
                    }
                    if (objects.length === 0) {
                        $("#data_table_body").empty();
                        $("#data_table_body").append('<tr>' +
                            '<td></td>' +
                            '<td></td>' +
                            '<td></td>' +
                            '<td></td>' +
                            '<td></td>' +
                            '</tr>')
                    } else {
                        $("#data_table_body").empty();
                        $.each(objects, function (key, value) {

                            if (objects.length === 0) {
                                $("#data_table_body").empty();
                                $("#data_table_body").append('<tr>' +
                                    '<td></td>' +
                                    '<td></td>' +
                                    '<td></td>' +
                                    '<td></td>' +
                                    '<td></td>' +
                                    '</tr>')
                            } else {
                                $("#data_table_body").empty();
                                //add_to_item_table(objects);
                                $.each(objects, function (key, value) {
                                    $("#data_table_body").append('<tr class="even pointer">' +
                                        '<td>' + eval(key + 1) + '</td>' +
                                        '<td>' + value.class_name + '</td>' +
                                        '<td>' + value.section_name + '</td>' +
                                        '<td>' + value.subject_name + '</td>' +
                                        '<td>' + value.teacher_name + '</td>' +
                                        '</tr>'
                                    )
                                })
                            }
                        })

                    }
                }
            })
        });

    });
